import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Table, Button } from 'react-bootstrap';
import ProfileDetails from './viewprofile'; // Import the new component
import Nav from './navbar';

function Profile() {
  const [data, Setprofiles] = useState([]);
  const [selectedProfile, setSelectedProfile] = useState(null); // Track selected profile

  useEffect(() => {
    axios.get('https://randomuser.me/api/?results=12')
      .then((res) => Setprofiles(res.data.results))
      .catch((err) => console.log(err));
  }, []);

  const handleViewDetails = (profile) => {
    setSelectedProfile(profile);
  };

  return (
    <div>
      {/* Your table code */}
      {/* ... */}
      <Table striped bordered hover>
        <tbody>
          {data.map((user, index) => (
            <tr key={index} className="table-light">
              {/* ... */}
              <td>
                <Button variant="info" size="sm" className="mr-2" onClick={() => handleViewDetails(user)}>View</Button>
                {/* ... */}
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      {/* Display profile details when selectedProfile is not null */}
      {selectedProfile && <ProfileDetails profile={selectedProfile} />}
    </div>
  );
}

export default Profile;



